import color from '@material-ui/core/colors/deepOrange';

import React, { Component } from 'react';


function getData(url = ``, data = {}) {
    // Default options are marked with *
    return fetch(url, {
        method: "GET", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, cors, *same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json",

            // "Content-Type": "application/x-www-form-urlencoded",
        },
        redirect: "follow", // manual, *follow, error
        referrer: "no-referrer", // no-referrer, *client
        //body: JSON.stringify(data), // body data type must match "Content-Type" header
    })
        .then(response => response.json()); // parses response to JSON
}




class Build extends Component {



    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
        this.updateInput = this.updateInput.bind(this);


        //this.getData = this.getData.bind(this);
        this.handleSubmit5 = this.handleSubmit5.bind(this);
    }



    componentDidMount() {
        var th = this;
        //this.serverRequest = axios.get(this.props.source)
        getData(`http://localhost/material-dashboard-react-v1.5.0/src/views/Typograghy/search.php`)
            .then(function (event) {
                th.setState({
                    data: event//.data
                });
            })
    }

    componentWillUnmount() {
        //this.serverRequest.abort();
    }





    updateInput = (event) => {
        let state = {};
        state[event.target.name] = event.target.value;
        this.setState(state);
    }



    handleSubmit5 = (event) => {
        event.preventDefault();
        //alert('Handle it on your own');
        console.log(this.state);
        getData(`http://localhost/material-dashboard-react-v1.5.0/src/views/Typograghy/search.php`, this.state)
            .then(data => console.log(JSON.stringify(data)))
            .catch(error => console.error(error));

    }
    contents=(event)=>{
        event.preventDefault();
        //alert('Handle it on your own');
        console.log(this.state);
        <div className="Table">
                <table style={{background:"pink",border:" 1px solid black"}} onChange={this.props.get}>
                    <thead style={{border:" 1px solid black",background:"gray"}}>
                    <tr>
                    <th>First Name</th>
                    <th>Parent Name</th>
                    <th>Last Name</th>

                    <th>StudentID </th>
                    <th>ClassID </th>
                    <th>ParentID </th>

                    <th>City</th>
                    <th>Phone</th>
                    <th>Address</th>
                    <th>Date Of Birth </th>


                    </tr>
                    </thead>

                    <tbody>
                    {
                    this.state.data.map(item=>
                    <tr key={item.id3}>
                        <td>{item.fname}</td>
                        <td>{item.mname}</td>
                        <td>{item.lname}</td>
                        <td>{item.id_st}</td>
                        <td>{item.id}</td>
                        <td>{item.p_id}</td>

                        <td>{item.city}</td>
                        <td>{item.phone}</td>
                        <td>{item.address}</td>
                        <td>{item.DateBirth}</td>
                    </tr>
                    )
                    }
                    </tbody>

                </table>
            </div>

    }



    render() {

        const contents = this.state.data.map(item => {
            //change the title and location key based on your API
            return(<tr key={item.id}>

            <tr>
               <td>{item.fname}</td>
                <td>{item.mname}</td>
                <td>{item.lname}</td>
                <td>{item.id}</td>
                <td>{item.subject}</td>
                <td>{item.city}</td>
                <td>{item.phone}</td>
                <td>{item.address}</td>
                <td>{item.DateBrith}</td>
                </tr>
            </tr>)
        })
        return (
            <div className="Table">
                <table style={{background:"pink",border:" 1px solid black"}} onChange={this.props.get}>
                    <thead style={{border:" 1px solid black",background:"gray"}}>
                    <tr>
                    <th>First Name</th>
                    <th>Parent Name</th>
                    <th>Last Name</th>
                    <th>ID Number</th>
                    <th>Subject</th>
                    <th>City</th>
                    <th>Phone</th>
                    <th>Address</th>
                    <th>Date Of Birth </th>


                    </tr>
                    </thead>

                    <tbody>
                    {
                    this.state.data.map(item=>
                    <tr key={item.id2}>
                        <td>{item.fname}</td>
                        <td>{item.mname}</td>
                        <td>{item.lname}</td>
                        <td>{item.id}</td>
                        <td>{item.subject}</td>
                        <td>{item.city}</td>
                        <td>{item.phone}</td>
                        <td>{item.address}</td>
                        <td>{item.DateBirth}</td>
                    </tr>
                    )
                    }
                    </tbody>

                </table>
            </div>



        );
    }
}





export default Build

