import BugReport from "@material-ui/icons/BugReport";
import InputLabel from "@material-ui/core/InputLabel";

import React,{Component} from 'react';

import Card from "components/Card/Card.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import CardBody from "components/Card/CardBody.jsx";
import CardHeader from "components/Card/CardHeader.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import CustomTabs from "components/CustomTabs/CustomTabs.jsx";

import InputForm from './InputForm';




export default class Grades extends React.Component{
    
    constructor(props){
        super(props);

    }


    render(){

        const styleInput = {
            width: "150%",
            alignContent: "center",
            height: "25px",
            margin: "3px 0",
            border: "1px solid #ccc",
            borderBottomLeftRadius: "10px",
            borderBottomRightRadius: "10px",
            borderTopRightRadius: "10px",
            borderTopLeftRadius: "10px",
            
          };
          
        return(
            <center>
            <GridContainer>
                <GridItem xs={12} sm={12} md={6}>
                <CustomTabs
                headerColor="info"
                tabs={[
                  {
                    tabName: "Create_An_Exam",
                    tabIcon: BugReport,
                    tabContent: (
                        <GridContainer justify="center">
                            <form /*action="Edit.php" onSubmit={this.handleSubmit}style={{ alignContent: "Center" }}*/  >
                             
                                    
                                <InputForm inputType="text" inputKey="level" inputLabel="Level " updateInput={this.updateInput} />
                                <InputForm inputType="text" inputKey="subject" inputLabel="Subject  " updateInput={this.updateInput} />
                               
           
                                
                                
                                <div style={{ display: 'flex', width: '100%' }}>
                                    <GridItem xs={12} sm={6} md={6} style={{ textAlign: "center" }}>
                                   <inputLabel style={{ color: "#000", alignContent: "left" }}> Semester  </inputLabel>
                                        <select  required style={styleInput}>
                                            <option></option>
                                            <option>First_Semester</option>
                                            <option>Second_Semester</option>
                                        </select>
                                    </GridItem>
                                </div>

                                <InputForm inputType="text" inputKey="max" inputLabel="Max_Limit " updateInput={this.updateInput} />
                                <InputForm inputType="text" inputKey="min" inputLabel="Min_Limit  " updateInput={this.updateInput} />

                                <div style={{ display: 'flex', width: '100%' }}>
                                    <GridItem xs={12} sm={6} md={12} style={{ textAlign: "center" }}>
                                    <label style={{ color: "#000", alignContent: "Center",width:"60%" }}> Type_Exam </label>
                                        <select style={{
                                            width: "50%",
                                            alignContent: "Center",
                                            height: "25px",
                                            margin: "3px 0",
                                            border: "1px solid #ccc",
                                            borderBottomLeftRadius: "10px",
                                            borderBottomRightRadius: "10px",
                                            borderTopRightRadius: "10px",
                                            borderTopLeftRadius: "10px",

                                        }}>
                                            <option></option>
                                            <option>Daily_Exam</option>
                                            <option>Mid_Exam</option>
                                            <option>Final_Exam</option>
                                        </select>
                                    </GridItem>
                                </div>

                                
                                <InputForm inputType="date" inputKey="date" inputLabel="Date_Of_Exam : " updateInput={this.updateInput} />

                            </form>
                        </GridContainer>
                    ),
                  }
                ]}

                >

                </CustomTabs>
                </GridItem>
            </GridContainer>
            </center>
        )
    }
}
